import React, { useEffect, useRef } from 'react';
import { Loader } from '@googlemaps/js-api-loader';
import { MapComponentProps } from '../types';

interface ExtendedMapComponentProps extends MapComponentProps {
  onMapReady?: (map: google.maps.Map) => void;
}

const MapComponent: React.FC<ExtendedMapComponentProps> = ({
  apiKey,
  selectedRoute,
  routes,
  onMapReady
}) => {
  const mapRef = useRef<HTMLDivElement>(null);
  const mapInstanceRef = useRef<google.maps.Map | null>(null);
  const markersRef = useRef<google.maps.Marker[]>([]);
  const polylinesRef = useRef<google.maps.Polyline[]>([]);

  // Initialize Google Maps
  useEffect(() => {
    if (!apiKey || !mapRef.current || mapInstanceRef.current) return;

    const initMap = async () => {
      try {
        const loader = new Loader({
          apiKey,
          version: 'weekly',
          libraries: ['places']
        });

        await loader.load();

        if (!mapRef.current) {
          return;
        }

        const map = new google.maps.Map(mapRef.current, {
          center: { lat: 40.7128, lng: -74.0060 }, // Default to NYC
          zoom: 13,
          styles: [
            {
              featureType: 'poi',
              elementType: 'labels',
              stylers: [{ visibility: 'off' }]
            }
          ],
          mapTypeControl: false,
          fullscreenControl: false,
          streetViewControl: false
        });

        mapInstanceRef.current = map;
        onMapReady?.(map);
      } catch (error) {
        console.error('MapComponent: Failed to load Google Maps:', error);
        console.error('MapComponent: Error details:', error instanceof Error ? error.message : 'Unknown error');
      }
    };

    initMap();

    // Cleanup function
    return () => {
      if (mapInstanceRef.current) {
        mapInstanceRef.current = null;
      }
    };
  }, [apiKey, onMapReady]);

  // Clear existing markers and polylines
  const clearMap = () => {
    markersRef.current.forEach(marker => marker.setMap(null));
    polylinesRef.current.forEach(polyline => polyline.setMap(null));
    markersRef.current = [];
    polylinesRef.current = [];
  };

  // Draw routes on map
  useEffect(() => {
    if (!mapInstanceRef.current || routes.length === 0) return;

    clearMap();

    routes.forEach((route) => {
      if (route.path.length < 2) return;

      const isSelected = selectedRoute?.id === route.id;
      const color = isSelected ? '#f2751a' : '#6b7280';
      const weight = isSelected ? 4 : 2;

      const path = route.path.map(point => new google.maps.LatLng(point.lat, point.lng));

      const polyline = new google.maps.Polyline({
        path,
        geodesic: true,
        strokeColor: color,
        strokeOpacity: isSelected ? 1.0 : 0.7,
        strokeWeight: weight,
        map: mapInstanceRef.current
      });

      polylinesRef.current.push(polyline);

      // Add markers for photo spots
      route.photoSpots.forEach((spot) => {
        if (!spot.location) return;

        const marker = new google.maps.Marker({
          position: new google.maps.LatLng(spot.location.lat, spot.location.lng),
          map: mapInstanceRef.current,
          title: spot.name,
          icon: {
            url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <circle cx="12" cy="12" r="10" fill="${isSelected ? '#f2751a' : '#6b7280'}" stroke="white" stroke-width="2"/>
                <path d="M8 12l2 2 4-4" stroke="white" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
              </svg>
            `),
            scaledSize: new google.maps.Size(24, 24),
            anchor: new google.maps.Point(12, 12)
          }
        });

        const infoWindow = new google.maps.InfoWindow({
          content: `
            <div class="p-3">
              <h3 class="font-semibold text-gray-900">${spot.name}</h3>
              <div class="flex items-center mt-1">
                <span class="text-yellow-500">★</span>
                <span class="text-sm text-gray-600 ml-1">${spot.rating}</span>
                <span class="text-sm text-gray-500 ml-2">•</span>
                <span class="text-sm text-gray-500 ml-2">${spot.distance}</span>
              </div>
            </div>
          `
        });

        marker.addListener('click', () => {
          infoWindow.open(mapInstanceRef.current, marker);
        });

        markersRef.current.push(marker);
      });
    });

    // Fit bounds to show all routes
    if (routes.length > 0) {
      const bounds = new google.maps.LatLngBounds();
      routes.forEach(route => {
        route.path.forEach(point => {
          bounds.extend(new google.maps.LatLng(point.lat, point.lng));
        });
      });
      mapInstanceRef.current.fitBounds(bounds);
    }
  }, [routes, selectedRoute]);

  if (!apiKey) {
    return (
      <div className="relative w-full h-full bg-gray-100">
        <div className="w-full h-full flex items-center justify-center">
          <div className="text-center">
            <div className="bg-white rounded-lg shadow-lg p-8 max-w-md mx-auto">
              <div className="text-6xl mb-4">🗺️</div>
              <h3 className="text-xl font-semibold text-gray-900 mb-2">
                Interactive Map
              </h3>
              <p className="text-gray-600 mb-4">
                {selectedRoute ? (
                  <>
                    Showing <strong>{selectedRoute.name}</strong> with {selectedRoute.photoSpots.length} photo spots
                  </>
                ) : (
                  'Enter origin and destination to see photo-worthy routes'
                )}
              </p>

              {selectedRoute && (
                <div className="bg-primary-50 border border-primary-200 rounded-lg p-4">
                  <div className="flex items-center justify-between mb-2">
                    <span className="font-medium text-primary-900">Photo Score</span>
                    <span className="text-2xl font-bold text-primary-600">
                      {selectedRoute.photoScore}%
                    </span>
                  </div>
                  <div className="w-full bg-primary-200 rounded-full h-2">
                    <div
                      className="bg-primary-500 h-2 rounded-full transition-all duration-300"
                      style={{ width: `${selectedRoute.photoScore}%` }}
                    ></div>
                  </div>
                </div>
              )}

              <div className="mt-4 p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
                <p className="text-sm text-yellow-800">
                  <strong>Demo Mode:</strong> Add your Google Maps API key to see the interactive map
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

    return (
    <div className="relative w-full h-full">
      <div ref={mapRef} className="w-full h-full" />

      {/* Map Controls */}
      <div className="absolute top-4 right-4 bg-white rounded-lg shadow-lg p-3">
        <div className="text-xs font-medium text-gray-700 mb-2">Photo Score Legend</div>
        <div className="space-y-1">
          <div className="flex items-center space-x-2">
            <div className="w-3 h-3 bg-green-500 rounded-full"></div>
            <span className="text-xs">90%+ (Excellent)</span>
          </div>
          <div className="flex items-center space-x-2">
            <div className="w-3 h-3 bg-yellow-500 rounded-full"></div>
            <span className="text-xs">70-89% (Good)</span>
          </div>
          <div className="flex items-center space-x-2">
            <div className="w-3 h-3 bg-red-500 rounded-full"></div>
            <span className="text-xs">&lt;70% (Fair)</span>
          </div>
        </div>
      </div>

      {/* Route Info Panel */}
      {selectedRoute && (
        <div className="absolute bottom-4 left-4 bg-white rounded-lg shadow-lg p-4 max-w-sm">
          <h4 className="font-medium text-gray-900 mb-2">{selectedRoute.name}</h4>
          <div className="text-sm text-gray-600 space-y-1">
            <div>📸 Photo Score: {selectedRoute.photoScore}%</div>
            <div>📍 Distance: {selectedRoute.distance}</div>
            <div>⏱️ Duration: {selectedRoute.duration}</div>
            <div>📷 Photo Spots: {selectedRoute.photoSpots.length}</div>
          </div>
        </div>
      )}
    </div>
  );
};

export default MapComponent;
