// Enhanced responsive layout in App.tsx
<div className="flex h-[calc(100vh-80px)] lg:flex-row flex-col">
  {/* Enhanced Sidebar */}
  <div className="lg:w-96 w-full lg:h-full h-1/2 sidebar-container flex flex-col">
    <div className="p-6 lg:p-8 border-b border-gray-100">
      <div className="mb-6">
        <h2 className="text-xl font-bold text-gray-900 mb-2">Find Your Perfect Route</h2>
        <p className="text-gray-600 text-sm leading-relaxed">
          Discover the most photogenic walking paths with curated photo opportunities
        </p>
      </div>
      <SearchBar
        origin={origin}
        destination={destination}
        onOriginChange={setOrigin}
        onDestinationChange={setDestination}
        onSearch={handleSearch}
        loading={loading}
      />
    </div>

    <div className="flex-1 overflow-y-auto p-6 lg:p-8">
      {/* Your existing routes content with enhanced styling */}
    </div>
  </div>

  {/* Map with better responsive behavior */}
  <div className="flex-1 relative lg:h-full h-1/2">
    <MapComponent
      apiKey={GOOGLE_MAPS_API_KEY}
      selectedRoute={selectedRoute}
      routes={routes}
      onMapReady={handleMapReady}
    />
  </div>
</div>
