import React, { useState, useEffect } from 'react';
import { Camera, MapPin, Navigation, Star, AlertCircle } from 'lucide-react';
import MapComponent from './components/MapComponent.tsx';
import RouteCard from './components/RouteCard.tsx';
import SearchBar from './components/SearchBar.tsx';
import { Route, PhotoSpot } from './types.ts';
import { RouteService } from './services/routeService.ts';

const GOOGLE_MAPS_API_KEY = process.env.REACT_APP_GOOGLE_MAPS_API_KEY || '';

function App() {
  const [origin, setOrigin] = useState<string>('');
  const [destination, setDestination] = useState<string>('');
  const [routes, setRoutes] = useState<Route[]>([]);
  const [loading, setLoading] = useState<boolean>(false);
  const [selectedRoute, setSelectedRoute] = useState<Route | null>(null);
  const [error, setError] = useState<string>('');
  const [mapInstance, setMapInstance] = useState<google.maps.Map | null>(null);
  const [routeService, setRouteService] = useState<RouteService | null>(null);

  // Initialize Google Maps when API key is available
  useEffect(() => {
    console.log('API Key available:', !!GOOGLE_MAPS_API_KEY);
    console.log('API Key length:', GOOGLE_MAPS_API_KEY?.length);

    if (!GOOGLE_MAPS_API_KEY) {
      setError('Google Maps API key is required. Please add REACT_APP_GOOGLE_MAPS_API_KEY to your .env.local file.');
      return;
    }

    const initGoogleMaps = async () => {
      try {
        console.log('Loading Google Maps API...');
        const { Loader } = await import('@googlemaps/js-api-loader');

        const loader = new Loader({
          apiKey: GOOGLE_MAPS_API_KEY,
          version: 'weekly',
          libraries: ['places']
        });

        console.log('Google Maps loader created, loading...');
        await loader.load();
        console.log('Google Maps API loaded successfully');

        // Create a temporary map instance for the service
        const tempMap = new google.maps.Map(document.createElement('div'));
        const service = new RouteService(tempMap);
        setRouteService(service);
        setError('');
        console.log('RouteService initialized successfully');
      } catch (err) {
        console.error('Google Maps initialization error:', err);
        setError(`Failed to load Google Maps API: ${err instanceof Error ? err.message : 'Unknown error'}. Please check your API key and enabled services.`);
      }
    };

    initGoogleMaps();
  }, [GOOGLE_MAPS_API_KEY, routeService]);

  const handleSearch = async () => {
    if (!origin || !destination) {
      setError('Please enter both origin and destination.');
      return;
    }

    if (!routeService) {
      setError('Google Maps API is not ready. Please check your API key and try refreshing the page.');
      return;
    }

    setLoading(true);
    setError('');
    setRoutes([]);
    setSelectedRoute(null);

    try {
      const foundRoutes = await routeService.findPhotoWorthyRoutes(origin, destination);

      if (foundRoutes.length === 0) {
        setError('No routes found between these locations. Please try different addresses.');
        return;
      }

      setRoutes(foundRoutes);
      setSelectedRoute(foundRoutes[0]);
    } catch (err) {
      console.error('Route search error:', err);
      setError('Failed to find routes. Please check your addresses and try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleMapReady = (map: google.maps.Map) => {
    setMapInstance(map);
  };

  return (
    <div className="min-h-screen bg-gray-50 font-inter">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center space-x-3">
              <div className="bg-primary-500 p-2 rounded-lg">
                <Camera className="h-6 w-6 text-white" />
              </div>
              <div>
                <h1 className="text-xl font-bold text-gray-900">SnapRoute</h1>
                <p className="text-sm text-gray-500">Photo-worthy route planner</p>
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2 text-sm text-gray-600">
                <Star className="h-4 w-4" />
                <span>Best Use of Places API</span>
              </div>
            </div>
          </div>
        </div>
      </header>

      {/* Error Banner */}
      {error && (
        <div className="bg-red-50 border-b border-red-200 px-4 py-3">
          <div className="flex items-center space-x-2 text-red-800">
            <AlertCircle className="h-4 w-4" />
            <span className="text-sm">{error}</span>
          </div>
        </div>
      )}

      <div className="flex h-[calc(100vh-64px)]">
        {/* Sidebar */}
        <div className="w-96 bg-white border-r border-gray-200 flex flex-col">
          <div className="p-6 border-b border-gray-200">
            <SearchBar
              origin={origin}
              destination={destination}
              onOriginChange={setOrigin}
              onDestinationChange={setDestination}
              onSearch={handleSearch}
              loading={loading}
            />
          </div>

          <div className="flex-1 overflow-y-auto p-6">
            {routes.length > 0 ? (
              <div className="space-y-4">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">
                  Photo-Worthy Routes ({routes.length})
                </h3>
                {routes.map((route) => (
                  <RouteCard
                    key={route.id}
                    route={route}
                    isSelected={selectedRoute?.id === route.id}
                    onSelect={() => setSelectedRoute(route)}
                  />
                ))}
              </div>
            ) : !loading ? (
              <div className="text-center py-12">
                <Camera className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">
                  Find Instagrammable Routes
                </h3>
                <p className="text-gray-500 mb-4">
                  Enter your origin and destination to discover the most photogenic walking paths.
                </p>
                {!GOOGLE_MAPS_API_KEY ? (
                  <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
                    <h4 className="font-medium text-yellow-900 mb-2">Setup Required</h4>
                    <p className="text-sm text-yellow-800">
                      Add your Google Maps API key to see real photo-worthy routes with actual landmarks and photo spots.
                    </p>
                  </div>
                ) : (
                  <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                    <h4 className="font-medium text-blue-900 mb-2">Try these examples:</h4>
                    <div className="text-sm text-blue-800 space-y-1">
                      <div>• "Times Square, NYC" → "Brooklyn Bridge"</div>
                      <div>• "Sagrada Familia, Barcelona" → "Park Güell"</div>
                      <div>• "Eiffel Tower, Paris" → "Louvre Museum"</div>
                    </div>
                  </div>
                )}
              </div>
            ) : (
              <div className="text-center py-12">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-500 mx-auto mb-4"></div>
                <p className="text-gray-600">Finding photo-worthy routes...</p>
              </div>
            )}
          </div>
        </div>

        {/* Map */}
        <div className="flex-1">
          {/* <SimpleMapTest apiKey={GOOGLE_MAPS_API_KEY} /> */}
          <MapComponent
            apiKey={GOOGLE_MAPS_API_KEY}
            selectedRoute={selectedRoute}
            routes={routes}
            onMapReady={handleMapReady}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
