import React, { useState, useEffect, useCallback } from 'react';
import { Camera, Star, AlertCircle, MapPin, Navigation, Search, Sparkles, TrendingUp } from 'lucide-react';
import MapComponent from './components/MapComponent.tsx';
import RouteCard from './components/RouteCard.tsx';
import SearchBar from './components/SearchBar.tsx';
import { Route } from './types.ts';
import { RouteService } from './services/routeService.ts';

const GOOGLE_MAPS_API_KEY = process.env.REACT_APP_GOOGLE_MAPS_API_KEY || '';

function App() {
  const [origin, setOrigin] = useState<string>('');
  const [destination, setDestination] = useState<string>('');
  const [routes, setRoutes] = useState<Route[]>([]);
  const [loading, setLoading] = useState<boolean>(false);
  const [selectedRoute, setSelectedRoute] = useState<Route | null>(null);
  const [error, setError] = useState<string>('');
  const [routeService, setRouteService] = useState<RouteService | null>(null);

  // Initialize Google Maps when API key is available
  useEffect(() => {
    if (!GOOGLE_MAPS_API_KEY) {
      setError('Google Maps API key is required. Please add REACT_APP_GOOGLE_MAPS_API_KEY to your .env.local file.');
      return;
    }

    if (routeService) {
      return; // Already initialized
    }

    const initGoogleMaps = async () => {
      try {
        const { Loader } = await import('@googlemaps/js-api-loader');

        const loader = new Loader({
          apiKey: GOOGLE_MAPS_API_KEY,
          version: 'weekly',
          libraries: ['places']
        });

        await loader.load();

        // Create a temporary map instance for the service
        const tempMap = new google.maps.Map(document.createElement('div'));
        const service = new RouteService(tempMap);
        setRouteService(service);
        setError('');
      } catch (err) {
        console.error('Google Maps initialization error:', err);
        setError(`Failed to load Google Maps API: ${err instanceof Error ? err.message : 'Unknown error'}. Please check your API key and enabled services.`);
      }
    };

    initGoogleMaps();
  }, [routeService]);

  const handleSearch = async () => {
    if (!origin || !destination) {
      setError('Please enter both origin and destination.');
      return;
    }

    if (!routeService) {
      setError('Google Maps API is not ready. Please check your API key and try refreshing the page.');
      return;
    }

    setLoading(true);
    setError('');
    setRoutes([]);
    setSelectedRoute(null);

    try {
      const foundRoutes = await routeService.findPhotoWorthyRoutes(origin, destination);

      if (foundRoutes.length === 0) {
        setError('No routes found between these locations. Please try different addresses.');
        return;
      }

      setRoutes(foundRoutes);
      setSelectedRoute(foundRoutes[0]);
    } catch (err) {
      console.error('Route search error:', err);
      setError('Failed to find routes. Please check your addresses and try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleMapReady = useCallback((map: google.maps.Map) => {
    // Map is ready, but we don't need to store it in state
    console.log('Map is ready:', map);
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-purple-50 to-pink-50">
      {/* Header */}
      <header className="glass-card border-b border-white/20 sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-20">
            <div className="flex items-center space-x-4">
              <div className="relative">
                <div className="bg-gradient-to-r from-primary-500 to-primary-600 p-3 rounded-2xl shadow-lg">
                  <Camera className="h-8 w-8 text-white" />
                </div>
                <div className="absolute -top-1 -right-1 bg-yellow-400 rounded-full p-1">
                  <Sparkles className="h-3 w-3 text-yellow-900" />
                </div>
              </div>
              <div>
                <h1 className="text-2xl font-bold gradient-text">SnapRoute</h1>
                <p className="text-sm text-gray-600 font-medium">Discover Instagram-worthy walking paths</p>
              </div>
            </div>
            <div className="flex items-center space-x-6">
              <div className="hidden md:flex items-center space-x-2 text-sm text-gray-600 bg-white/50 px-4 py-2 rounded-full">
                <TrendingUp className="h-4 w-4 text-primary-500" />
                <span className="font-medium">Best Use of Places API</span>
              </div>
              <div className="flex items-center space-x-2 text-sm text-gray-600 bg-white/50 px-4 py-2 rounded-full">
                <Star className="h-4 w-4 text-yellow-500 fill-current" />
                <span className="font-medium">Photo-Worthy Routes</span>
              </div>
            </div>
          </div>
        </div>
      </header>

      {/* Error Banner */}
      {error && (
        <div className="bg-red-50 border-b border-red-200 px-4 py-4">
          <div className="max-w-7xl mx-auto flex items-center space-x-3 text-red-800">
            <AlertCircle className="h-5 w-5 flex-shrink-0" />
            <span className="text-sm font-medium">{error}</span>
          </div>
        </div>
      )}

      <div className="flex h-[calc(100vh-80px)]">
        {/* Sidebar */}
        <div className="w-96 bg-white/80 backdrop-blur-xl border-r border-white/20 flex flex-col">
          <div className="p-8 border-b border-gray-100">
            <div className="mb-6">
              <h2 className="text-xl font-bold text-gray-900 mb-2">Find Your Perfect Route</h2>
              <p className="text-gray-600 text-sm">Enter your start and end points to discover the most photogenic walking paths</p>
            </div>
            <SearchBar
              origin={origin}
              destination={destination}
              onOriginChange={setOrigin}
              onDestinationChange={setDestination}
              onSearch={handleSearch}
              loading={loading}
            />
          </div>

          <div className="flex-1 overflow-y-auto p-8">
            {routes.length > 0 ? (
              <div className="space-y-6">
                <div className="flex items-center justify-between">
                  <h3 className="text-lg font-bold text-gray-900">
                    Photo-Worthy Routes
                  </h3>
                  <div className="bg-primary-50 text-primary-700 px-3 py-1 rounded-full text-sm font-medium">
                    {routes.length} found
                  </div>
                </div>
                <div className="space-y-4">
                  {routes.map((route) => (
                    <RouteCard
                      key={route.id}
                      route={route}
                      isSelected={selectedRoute?.id === route.id}
                      onSelect={() => setSelectedRoute(route)}
                    />
                  ))}
                </div>
              </div>
            ) : !loading ? (
              <div className="text-center py-16">
                <div className="relative mb-8">
                  <div className="bg-gradient-to-r from-primary-100 to-primary-200 p-6 rounded-3xl inline-block">
                    <Camera className="h-16 w-16 text-primary-600" />
                  </div>
                  <div className="absolute -top-2 -right-2 bg-yellow-400 rounded-full p-2">
                    <Sparkles className="h-4 w-4 text-yellow-900" />
                  </div>
                </div>
                <h3 className="text-2xl font-bold text-gray-900 mb-4">
                  Ready to Discover?
                </h3>
                <p className="text-gray-600 mb-8 max-w-sm mx-auto leading-relaxed">
                  Enter your origin and destination to discover the most Instagram-worthy walking paths with stunning photo opportunities.
                </p>
                {!GOOGLE_MAPS_API_KEY ? (
                  <div className="glass-card p-6 rounded-2xl border border-yellow-200">
                    <h4 className="font-bold text-yellow-900 mb-3">Setup Required</h4>
                    <p className="text-sm text-yellow-800 leading-relaxed">
                      Add your Google Maps API key to see real photo-worthy routes with actual landmarks and photo spots.
                    </p>
                  </div>
                ) : (
                  <div className="glass-card p-6 rounded-2xl border border-blue-200">
                    <h4 className="font-bold text-blue-900 mb-3">Try These Examples</h4>
                    <div className="text-sm text-blue-800 space-y-2">
                      <div className="flex items-center space-x-2">
                        <MapPin className="h-4 w-4" />
                        <span>"Times Square, NYC" → "Brooklyn Bridge"</span>
                      </div>
                      <div className="flex items-center space-x-2">
                        <MapPin className="h-4 w-4" />
                        <span>"Sagrada Familia, Barcelona" → "Park Güell"</span>
                      </div>
                      <div className="flex items-center space-x-2">
                        <MapPin className="h-4 w-4" />
                        <span>"Eiffel Tower, Paris" → "Louvre Museum"</span>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            ) : (
              <div className="text-center py-16">
                <div className="relative mb-8">
                  <div className="bg-gradient-to-r from-primary-100 to-primary-200 p-6 rounded-3xl inline-block animate-pulse-slow">
                    <Camera className="h-16 w-16 text-primary-600" />
                  </div>
                </div>
                <h3 className="text-xl font-bold text-gray-900 mb-4">
                  Finding Your Perfect Route
                </h3>
                <p className="text-gray-600">Analyzing photo opportunities and scenic paths...</p>
              </div>
            )}
          </div>
        </div>

        {/* Map */}
        <div className="flex-1 relative">
          <MapComponent
            apiKey={GOOGLE_MAPS_API_KEY}
            selectedRoute={selectedRoute}
            routes={routes}
            onMapReady={handleMapReady}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
