import React, { useEffect, useRef } from 'react';
import { Loader } from '@googlemaps/js-api-loader';

interface SimpleMapTestProps {
  apiKey: string;
}

const SimpleMapTest: React.FC<SimpleMapTestProps> = ({ apiKey }) => {
  const mapRef = useRef<HTMLDivElement>(null);
  const [status, setStatus] = React.useState<string>('Initializing...');
  const [error, setError] = React.useState<string>('');

  useEffect(() => {
    if (!apiKey || !mapRef.current) return;

    const initMap = async () => {
      try {
        setStatus('Loading Google Maps API...');

        const loader = new Loader({
          apiKey,
          version: 'weekly',
          libraries: ['places']
        });

        setStatus('Creating map...');
        await loader.load();

        if (!mapRef.current) {
          setError('Map container not found');
          return;
        }

        const map = new google.maps.Map(mapRef.current, {
          center: { lat: 40.7128, lng: -74.0060 },
          zoom: 13,
          mapTypeControl: false,
          fullscreenControl: false,
          streetViewControl: false
        });

        setStatus('Map loaded successfully!');

        // Add a marker to verify the map is working
        new google.maps.Marker({
          position: { lat: 40.7128, lng: -74.0060 },
          map: map,
          title: 'Test Marker'
        });

      } catch (err) {
        const errorMsg = err instanceof Error ? err.message : 'Unknown error';
        setError(`Failed to load map: ${errorMsg}`);
        console.error('SimpleMapTest error:', err);
      }
    };

    initMap();
  }, [apiKey]);

  return (
    <div className="p-4">
      <h3 className="text-lg font-semibold mb-4">Simple Map Test</h3>

      {status && (
        <div className="mb-4 p-3 bg-blue-50 border border-blue-200 rounded">
          <p className="text-blue-800">{status}</p>
        </div>
      )}

      {error && (
        <div className="mb-4 p-3 bg-red-50 border border-red-200 rounded">
          <p className="text-red-800">{error}</p>
        </div>
      )}

      <div
        ref={mapRef}
        className="w-full h-64 border border-gray-300 rounded"
        style={{ minHeight: '256px' }}
      />
    </div>
  );
};

export default SimpleMapTest;
